    <script>
        function initMap() {
            var myLatLng = {
                lat: {{ $locations->lat }},
                lng: {{ $locations->lng }}
            };

            var map = new google.maps.Map(document.getElementById('map'), {
                center: myLatLng,
                zoom: 17
            });

            var marker = new google.maps.Marker({
                position: myLatLng,
                map: map,
                title: 'Local!',
                draggable: false
            });
        }
    </script>
    <style type="text/css">
        #map {
            width: 100%;
            height: 400px;
        }
    </style>
    <script src="https://maps.googleapis.com/maps/api/js?libraries=places&callback=initMap" async defer></script>
